import React from "react";
import { useEffect } from "react";
import Modal from "./Modal";
import useModals from "./useModals";

const AddSubtractModal = ({ show, handleClose, addSubtractAmount, sign }) => {
    const { currentCategoryId, handleCloseAddSubtractModal } = useModals();

    useEffect(() => {
        console.log('Modal open, currentCategoryId:', currentCategoryId);
    }, [currentCategoryId]);


    return (
        <Modal show={show} handleClose={handleClose}>
            <h2>{sign === '+' ? 'Add Amount' : 'Subtract Amount'}</h2>
            <form onSubmit={(e) => {
                e.preventDefault();
                const addValue = parseFloat(e.target.addValue.value) * (sign === '+' ? 1 : -1); // Get the value to add
                console.log('currentCategoryId in submit form: ', currentCategoryId); // Should log the correct category ID
                console.log('Add value: ', addValue); // Should log the correct value
                addSubtractAmount(currentCategoryId, addValue);
                e.target.reset();
                handleCloseAddSubtractModal();
            }}>
            <div className='info'>
                <label>
                Amount: 
                <input type='number' name='addValue' min='0.01' max='100000' step='0.01' required />
                </label>
            </div>
            <div className='modal-btns'>
                <button type='submit'>{sign === '+' ? 'Add' : 'Subtract'}</button>
            </div>
            </form>
        </Modal>
    );
};

export default AddSubtractModal;
