import React, {useState} from 'react'
import './App.css';
import CircularMeter from './CircularMeter';
import Modal from './Modal';

function App() {
  const [showModal, setShowModal] = useState(false);
  const [categories, setCategories] = useState([]);
  const [showAddModal, setShowAddModal] = useState(false);
  const [currentCategoryId, setCurrentCategoryId] = useState(null);

  const handleOpenModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const addCategory = (name, maxValue) => {
    const newCategory = { id: categories.length + 1, name, value: 0, maxValue };
    setCategories([...categories, newCategory]);
    setShowModal(false); // Close modal
  };

  const handleOpenAddModal = (id) => {
    setShowAddModal(true);
    setCurrentCategoryId(id);
  }

  const handleCloseAddModal = () => {
    setShowAddModal(false);
    setCurrentCategoryId(null);
  }

  return (
    <div className="App">
      <h1>Conscience Spending Plan</h1>

      <div className='category'>
        <CircularMeter value={25} maxValue={100}/>

        {categories.map((category) => (
          <div key={category.id} className='category-item'>
            <h2>{category.name}</h2>
            <CircularMeter value={category.value} maxValue={category.maxValue}/>
            <p>${category.value} / {category.maxValue}</p>
            <div>
              <button>Edit Amount</button>
              <button onClick={handleOpenAddModal}>+</button>
            </div>
          </div>
        ))}
      </div>

      <button onClick={handleOpenModal}>Add Category</button>

      <Modal show={showModal} handleClose={handleCloseModal}>
        <h2>Add New Category</h2>
        <form onSubmit={(e) => {
          e.preventDefault(); // Prevent the default form submission behavior
          const name = e.target.elements.categoryName.value; // Get the name from the input field
          const maxValue = parseInt(e.target.elements.valueInput.value); // Get the value from the input field
          addCategory(name, maxValue); // Make new category
        }}>
          <label>
            Name: 
            <input type='text' name='categoryName' required />
          </label>
          <label>
            Budget: 
            <input type='number' name='valueInput' min='1' max='100000' required />
          </label>
          <button type="submit">Add</button>
        </form>
      </Modal>

      <Modal show={showAddModal} handleClose={handleCloseAddModal}>
        <h2>Add Amount</h2>
        <form onSubmit={(e) => {
          e.preventDefault();
          const addValue = (e.target.addValue.value);
        }}>
          <label>
            Amount: 
            <input type='number' name='addValue' min='0.01' max='100000.00'/>
          </label>
          <button type='submite'>Add</button>
        </form>
      </Modal>
    </div>
  );
}

export default App;
